mapper.select:
SELECT COUNT(*) FROM tb_organization
AND tag LIKE CONCAT('%', #{tag}, '%')
AND name LIKE CONCAT('%', #{name}, '%')
AND parent_id = #{parentId}
AND created_by = #{createdBy}
AND deleted = 0

mapper.update:
UPDATE tb_organization
SET deleted = 1
WHERE id = #{id}

mapper.select:
SELECT FROM tb_organization WHERE id IN
#{id}
AND deleted = 0

SELECT id FROM tb_organization WHERE tag IN
#{tag}
AND deleted = 0
